import React from 'react';

import '../stylesheets/App.css';
import Question from './Question';
import Search from './Search';
import $ from 'jquery';

class QuestionView extends React.Component {
  constructor(){
    super();
    this.state = {
      searchQuery: '',
      questions: [],
      page: 1,
      totalQuestions: 0,
      categories: [],
      currentCategory: null,
    }
  }

  componentDidMount() {
    this.getQuestions();
  }

  getQuestions = () => {
    fetch(`/questions?page=${this.state.page}`)
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          questions: result.questions,
          totalQuestions: result.total_questions,
          categories: result.categories,
          currentCategory: result.current_category
        })
      },
      (error) => {
        alert('Unable to load questions. Please try your request again')
      }
      )
  }

  selectPage(num) {
    if (this.state.currentCategory) {
      this.getByCategory(this.state.currentCategory.id, num)
    } else if (this.state.searchQuery) {
      this.submitSearch(this.state.searchQuery, num)
    } else {
      this.setState({page: num}, () => this.getQuestions());
    }
  }

  createPagination(){
    let pageNumbers = [];
    let maxPage = Math.ceil(this.state.totalQuestions / 10)
    for (let i = 1; i <= maxPage; i++) {
      pageNumbers.push(
        <span
          key={i}
          className={`page-num ${i === this.state.page ? 'active' : ''}`}
          onClick={() => {this.selectPage(i)}}>{i}
        </span>)
    }
    console.log(pageNumbers)
    return pageNumbers;
  }

  getByCategory = (id, page = 1) => {
    fetch(`categories/${id}/questions?page=${page}`)
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          page: page,
          questions: result.questions,
          totalQuestions: result.total_questions,
          currentCategory: result.category
        })
        return;
      },
      (error)=> {
        alert('Unable to load questions. Please try your request again')
      }
    )
  }

  submitSearch = (searchTerm, page = 1) => {
    $.ajax({
      url: `/searchQuestions?page=${page}`, //TODO: update request URL
      type: "POST",
      dataType: 'json',
      contentType: 'application/json',
      data: JSON.stringify({searchTerm: searchTerm}),
      xhrFields: {
        withCredentials: true
      },
      crossDomain: true,
      success: (result) => {
        this.setState({
          searchQuery: searchTerm,
          page: page,
          questions: result.questions,
          totalQuestions: result.total_questions,
          currentCategory: result.current_category
        });
        return;
      },
      error: (error) => {
        alert('Unable to load questions. Please try your request again')
        return;
      }
    })
  }

  questionAction = (id) => (action) => {
    if(action === 'DELETE') {
      if(window.confirm('are you sure you want to delete the question?')) {
        $.ajax({
          url: `/questions/${id}`, //TODO: update request URL
          type: "DELETE",
          success: (result) => {
            this.getQuestions();
          },
          error: (error) => {
            alert('Unable to load questions. Please try your request again')
            return;
          }
        })
      }
    }
  }

  render() {
    return (
      <div className="question-view">
        <div className="categories-list">
          <h2 onClick={() => {this.getQuestions()}}>Categories</h2>
          <ul>
            {this.state.categories.map((category) => (
              <li key={category.id} onClick={() => {this.getByCategory(category.id)}}>
                {category.type}
                <img className="category" src={`${category.type.toLowerCase()}.svg`}/>
              </li>
            ))}
          </ul>
          <Search submitSearch={this.submitSearch}/>
        </div>
        <div className="questions-list">
          <h2>Questions</h2>
          {this.state.questions.map((q, ind) => (
            <Question
              key={q.id}
              question={q.question}
              answer={q.answer}
              category={this.state.categories[q.category - 1]}
              difficulty={q.difficulty}
              questionAction={this.questionAction(q.id)}
            />
          ))}
          <div className="pagination-menu">
            {this.createPagination()}
          </div>
        </div>

      </div>
    );
  }
}

export default QuestionView;
